import React, { Component } from 'react';
import './Login.css';


export default class Login extends Component {
    constructor(){
        super();
        this.state = {
                userName:'', 
                userPass:'',            
        }

    }

    
    

    render () {
        const {name} = this.props;
        const {loginInfo} = this.state;

       
        return(
            <div className = "Login">
            
                <input type = "text" placeholder= "User Name" onKeyUp = {this.onDoneName} />
                <input type = "password" placeholder="password" onKeyUp ={this.onDonePass} />
                <button  onClick = {() => this.checkLogin(loginInfo, name)}>Login </button>
            </div>
        )
    }
   



    checkLogin(loginInfo, name){

        


        name.map((loginUser) => {
           debugger;
            if(loginInfo.userName === loginUser.loginName  ){
                
                 console.log("success")
            }
          
            if(loginInfo.userPass !== loginUser.loginName){
                console.log("faild")
            }
        }) 

    
     }
}
