import React, { Component } from 'react';
import './Login.css';


export default class Login extends Component {
    


    

    render () {
        const {name} = this.props;
        let userPass = doneName.target.value;
        let userName = donePass.target.value;
        return(
            <div className = "Login">
            
                <input type = "text" placeholder= "User Name" onKeyUp = {this.doneName} />
                <input type = "password" placeholder="password" onKeyUp ={this.donePass} />
                <button value = "Login" onClick = {() => this.checkLogin(userName, userPass, name)}/>
            </div>
        )
    }


 


   



    checkLogin(userName, userPass, name){

        


        name.map((loginUser) => {
           debugger;
            if(userName === loginUser.loginName  ){
                
                 console.log("success")
            }
          
            if(userName !== loginUser.loginName){
                console.log("faild")
            }
        }) 

    
     }
}
