import React, { Component } from 'react';
import './Login.css';


export default class Login extends Component {
    constructor(state){
        super(state);
        this.state = {
                userName:"", 
                userPass:"",            
        }



    this.onDoneName = this.onDoneName.bind(this);    
    }

    
    

    render () {
        const {name} = this.props;

       
        return(
            <div className = "Login">
            
                <input type = "text" placeholder= "User Name" onKeyUp = {this.onDoneName} />
                <input type = "password" placeholder="password" onKeyUp ={this.onDonePass} />
                <button  onClick = {() => this.checkLogin( name)}>Login </button>
            </div>
        )
    }
   
    onDoneName(event){
        
        let text="";
        // console.log(event.target.value );
        text = event.target.value;
        text =text.trim();
        if( !text ){
            alert("enter user name"); 
        }
        this.setState({
            userName:text, 
            ...this.state.userPass, 
        })
        
    }



    checkLogin(name){
       
        
        for (var i=0 ; i< name.length; i++){           
            if(this.state.userName === name[i].loginName  ){
                
                 alert("success")

            }
          
            else if(this.state.userName !== loginUser.loginName){
                alert("faild")
            }
        }
    }
    
     }
